import React from 'react';
import { Link, useHistory } from 'react-router-dom';
import { useState, useEffect } from 'react';
import axios from "axios";
import AppUser from './models/AppUser';

const Register = (props) => {

    const history = useHistory();

    const [appUser, setAppUser] = useState(new AppUser());
    const [credentials, setCredentials] = useState('');

    const handleAppUser = (event) => {
        console.log(event.target.name);
        console.log(event.target.value);
        setAppUser({
            ...appUser,
            [event.target.name]: event.target.value
        });
    };

    const submitAppUser = (event) => {

        axios.post(`http://localhost:8082/addUser`, appUser)
            .then((response) => {
                console.log(response.data);
                localStorage.setItem('appUser', appUser);
                alert('You are registered successfully. Please login now.');
                history.push('/login'); // check this method to navigate 
            }).catch((error) => {
                console.log(error.response);
                setCredentials("Enter proper credentials.");
            });
        event.preventDefault();
    }
    return (
        <div className="container">
            <div className="col-4 mt-4" >
                <h1 className="display-4 text-danger">Register</h1>
                <form className="form form-group form-dark " onSubmit={submitAppUser}>
                    <div>
                        <input
                            type="text"
                            className="form-control"
                            name="userName"
                            id="userName"
                            className="form-control mb-3"
                            placeholder="Enter username"
                            value={appUser.userName}
                            onChange={handleAppUser}
                            required
                        />
                        <input
                            type="password"
                            className="form-control"
                            name="password"
                            id="password"
                            className="form-control mb-3"
                            placeholder="Enter password"
                            value={appUser.password}
                            onChange={handleAppUser} />
                        {/* <div class="form-group">
                            <select class="form-control mb-3" name="role" id="role" onChange={handleAppUser}>
                                <option value="Role">Select a role</option>
                                <option value="ADMIN">ADMIN</option>
                                <option value="EMPLOYEE">EMPLOYEE</option>
                                <option value="MANAGER">MANAGER</option>
                            </select>
                        </div> */}
                        <input
                            type="text"
                            className="form-control"
                            name="firstName"
                            id="firstName"
                            className="form-control mb-3"
                            placeholder="Enter FirstName"
                            value={appUser.firstName}
                            onChange={handleAppUser} />

                            <input
                            type="text"
                            className="form-control"
                            name="lastName"
                            id="lasttName"
                            className="form-control mb-3"
                            placeholder="Enter lasttName"
                            value={appUser.lastName}
                            onChange={handleAppUser} />

                        <input
                            type="number"
                            className="form-control"
                            name="contact"
                            id="contact"
                            className="form-control mb-3"
                            placeholder="Enter Contact Number"
                            value={appUser.contact}
                            onChange={handleAppUser} />


<input
                            type="text"
                            className="form-control"
                            name="mail"
                            id="mail"
                            className="form-control mb-3"
                            placeholder="Enter mail"
                            value={appUser.mail}
                            onChange={handleAppUser} />
                        <input
                            type="submit"
                            id="submit"
                            name="submit"
                            className="form-control btn btn-danger mb-3"
                            value="Register"
                            onClick={submitAppUser}
                        />

                    </div>
                </form>
                <p className="text-danger">{credentials}</p>
                <Link to="/login" className="btn btn-danger col-12">Already registered? Login</Link>
            </div>
            <div>
                {/* https://material.io/components/dialogs/web#alert-dialog */}
            </div>
        </div >

    )
}
export default Register;






